import React from "react";
import { Col, Row } from "react-bootstrap";

const Services = () => {
  const data = [
    {
      id: 1,
      img: "https://elementor-kits-02.nicdark.com/taxi-wordpress-elementor-kit/wp-content/uploads/sites/5/2024/03/black-traveler-man-with-suitcase-sitting-on-bench-in-airport-terminal-calling-and-looking-for-taxi.jpg",
      img2:"https://elementor-kits-02.nicdark.com/taxi-wordpress-elementor-kit/wp-content/uploads/sites/5/2024/01/square-01-800x800.jpg",
      height: "350px",
      height2: "350px",
      content:
        "Les chauffeurs privés offrent un service de transport personnalisé où ils conduisent les clients d'un endroit à un autre en utilisant leur propre véhicule. Que ce soit pour des rendez-vous d'affaires, des soirées entre amis ou des déplacements quotidiens, les chauffeurs privés garantissent un trajet sûr et confortable.",
      content2:
        "Les chauffeurs privés offrent un service de transport personnalisé où ils conduisent les clients d'un endroit à un autre en utilisant leur propre véhicule. Que ce soit pour des rendez-vous d'affaires, des soirées entre amis ou des déplacements quotidiens, les chauffeurs privés garantissent un trajet sûr et confortable.",
      title: "Tranferts aéroport",
      title2: "Tranferts aéroport",
    },




    {
      id: 2,
      img: "https://elementor-kits-02.nicdark.com/taxi-wordpress-elementor-kit/wp-content/uploads/sites/5/2024/01/square-01-800x800.jpg",
      height: "450px",
      content:
        "Nos chauffeurs locaux vous emmènent dans les endroits incontournables et les joyaux cachés de la région, offrant une expérience authentique et enrichissante à chaque étape de votre voyage.",
      title: "City tours",
    },
    {
      id: 3,
      img: "https://themeholy.com/wordpress/taxiar/wp-content/uploads/2023/09/service_3_4-4-1.jpg",
      height: "350px",
      content:
        "Les chauffeurs privés offrent un service de transport personnalisé où ils conduisent les clients d'un endroit à un autre en utilisant leur propre véhicule. Que ce soit pour des rendez-vous d'affaires, des soirées entre amis ou des déplacements quotidiens, les chauffeurs privés garantissent un trajet sûr et confortable.",
      title: "Déplacement professionnel",
    },
    {
      id: 4,
      img: "https://themeholy.com/wordpress/taxiar/wp-content/uploads/2023/09/service_3_2-4-1.jpg",
      height: "450px",
      content:
        "Les chauffeurs privés offrent un service de transport personnalisé où ils conduisent les clients d'un endroit à un autre en utilisant leur propre véhicule. Que ce soit pour des rendez-vous d'affaires, des soirées entre amis ou des déplacements quotidiens, les chauffeurs privés garantissent un trajet sûr et confortable.",
      title: "Transport régulier",
    },
    {
      id: 5,
      img: "https://elementor-kits-02.nicdark.com/taxi-wordpress-elementor-kit/wp-content/uploads/sites/5/2024/03/woman-cathcing-a-taxi.jpg",
      height: "450px",
      content:
        "Les chauffeurs privés offrent un service de transport personnalisé où ils conduisent les clients d'un endroit à un autre en utilisant leur propre véhicule. Que ce soit pour des rendez-vous d'affaires, des soirées entre amis ou des déplacements quotidiens, les chauffeurs privés garantissent un trajet sûr et confortable.",
      title: "Navette",
    },
  ];
  return (
    <div className="py-5">
      <Row>
        {data.map((item, index) => (
          <Col xl="3" className="p-0">
            <div className="position-relative" style={{ overflow: "hidden" }}>
              <div className="image-container">
                <img
                  width="100%"
                  height={item.height}
                  src={item.img}
                  alt={item.title}
                  className="img position-relative"
                />
                <div className="position-absolute top-0 w-100 h-100 d-flex justify-content-center align-items-end p-5 text-white fs-5 fw-medium">{item.title}</div>
                <div className="details-panel">
                  <h2>{item.title}</h2>
                  <p>{item.content}</p>
                </div>
              </div>
            </div>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Services;
