import {BaggageClaim, Gauge, Users2} from 'lucide-react'
import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Rides from '../components/Rides';
import Estimate from '../components/Estimate';


function Pricing() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
 

  const [distance, setDistance] = useState(0); 
  const [passagers, setPassagers] = useState(0); 
  const [bagages, setBagages] = useState(0); 
  const [tarif, setTarif] = useState(0);
  const [modalReady, setModalReady] = useState(false);
  
  const calculerTarif = () => {
    const nouveauTarif = distance * 2.6 + passagers * 20 + bagages * 1   // calcul du tarif

    setTarif(nouveauTarif); // Mettre à jour l'état du tarif avec le nouveau calcul
    // Ouvrir le modal après 5 secondes
    setTimeout(() => {
      setShow(true);
    }, 1000);
  };


  return (
    <div className='container py-5 my-5' id='tarifs'>
      <div className="fs-5 main-text">Nos tarifs</div>
      <div className="fs-1 fw-bold">Profitez nos tarifs <span className='background-main text-black p-3'>avantageux</span></div>
     
      <Rides/>
      
      <div className='mt-5'>
        <Estimate distance={distance} setDistance={setDistance} passagers={passagers} setPassagers={setPassagers} bagages={bagages} setBagages={setBagages} calculerTarif={calculerTarif}/>
        <p className='text-danger mt-3'>Veuillez noter que les prix peuvent varier en fonction de la météo, des heures,  de la destination, et du trafic, Merci de votre compréhension.</p>
      </div>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Prix total</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className='text-dark'>
            <div className='d-flex align-items-center fs-6 my-3'><Gauge size={30} strokeWidth={1.5} /> <span className='mx-2'>Distance en kilomètres : {distance}km</span></div>
            <div className='d-flex align-items-center fs-6 my-3'><Users2 size={30} strokeWidth={1.5} /> <span className='mx-2'>Passagers supplémentaires : {passagers}</span></div>
            <div className='d-flex align-items-center fs-6 my-3'><BaggageClaim size={30} strokeWidth={1.5} /> <span className='mx-2'>Nombre de bagages : {bagages}</span></div>
            <hr className='border-secondary'/>
            <div className='fs-1 fw-bold'>Prix estimé à : <span className='text-success'>{tarif} €</span></div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <button className='main-btn p-3 px-5' variant="primary" onClick={handleClose}>
            Continuer
          </button>
        </Modal.Footer>
      </Modal>
    </div>

  );
}

export default Pricing;
