import { BaggageClaim, Gauge, Users2 } from "lucide-react";
import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Rides from "../components/Rides";
import Estimate from "../components/Estimate";
import { useTranslation } from "react-i18next";

function Pricing() {
  const { t } = useTranslation();
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);

  const [distance, setDistance] = useState(0);
  const [passagers, setPassagers] = useState(0);
  const [bagages, setBagages] = useState(0);
  const [tarif, setTarif] = useState(0);
  const [calculEnCours, setCalculEnCours] = useState(false);

  const calculerTarif = () => {
    setCalculEnCours(true);

    setTimeout(() => {
      const nouveauTarif = distance * 2.6 + passagers * 20 + bagages * 1; // calcul du tarif
      setTarif(nouveauTarif);
      setCalculEnCours(false);
      setShow(true);
    }, 2000);
  };

  return (
    <div className="container py-5 my-5" id="tarifs">
      <div className="fs-5 text-dark">{t('our_rates')}</div>
      <div className="fs-1 fw-bold" dangerouslySetInnerHTML={{__html:  t('our_prices')}}>
       
      </div>

      <Rides />

      <div className="mt-5">
        <Estimate
          distance={distance}
          setDistance={setDistance}
          passagers={passagers}
          setPassagers={setPassagers}
          bagages={bagages}
          setBagages={setBagages}
          calculerTarif={calculerTarif}
          calculEnCours={calculEnCours}
        />
        <p className="text-danger mt-3">
          Veuillez noter que les prix peuvent varier en fonction de la météo,
          des heures, de la destination, et du trafic, Merci de votre
          compréhension.
        </p>
      </div>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Informations du trajet</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="text-dark p-3">
            <div className="d-flex align-items-center fs-6 my-3">
              <Gauge size={25} strokeWidth={1.5} />{" "}
              <span className="mx-2">
                Distance en kilomètres : {distance}km
              </span>
            </div>
            <div className="d-flex align-items-center fs-6 my-3">
              <Users2 size={25} strokeWidth={1.5} />{" "}
              <span className="mx-2">
                Passagers supplémentaires : {passagers}
              </span>
            </div>
            <div className="d-flex align-items-center fs-6 my-3">
              <BaggageClaim size={25} strokeWidth={1.5} />{" "}
              <span className="mx-2">Nombre de bagages : {bagages}</span>
            </div>
            <hr className="border-secondary" />
            <div className="fs-1 fw-bold">
              Prix total : <span className="text-success">{tarif} €</span>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <button
            className="main-btn p-3 px-5"
            variant="primary"
            onClick={handleClose}
          >
            Continuer
          </button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Pricing;
