import { ChevronLeftCircle, ChevronRightCircle, Quote } from 'lucide-react';
import React, { useState } from 'react';
import { Col, Row } from 'react-bootstrap';

const data = [
  { name: "Orélie H", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.",job:"Avocate" },

  { name: "Louis C", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.",job:"Photographe" },

  { name: "Karim B", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.",job:"Commercial" },
];

const Carousel = () => {
  const [index, setIndex] = useState(0);

  const next = () => {
    setIndex(prevIndex => (prevIndex + 1) % data.length);
  };

  const prev = () => {
    setIndex(prevIndex => (prevIndex === 0 ? data.length - 1 : prevIndex - 1));
  };

  return (
    <div className='container border border-main-5 my-5'>
        <Row>
          <Col xl="8" className='px-5'>
            <div className="position-relative py-5" style={{overflow:"hidden"}}>
              <div className="slider" style={{ transform: `translateX(-${index * 100}%)` }}>
                  {data.map((item, idx) => (
                      <div key={idx} className="slide">
                          <div className='main-text'><Quote size={100} strokeWidth={1}/></div>
                          <p className='my-3 fs-5'>{item.content}</p>
                          <div className='fs-3 text-black fw-bold'>{item.name}</div>
                          <p>{item.job}</p>
                      </div>
                  ))}
              </div>
              <div className='d-flex align-items-center mt-4'>
                <span role='button' onClick={prev}><ChevronLeftCircle size={60} strokeWidth={0.5}/></span>
                <span className='mx-3' role='button' onClick={next}><ChevronRightCircle size={60} strokeWidth={0.5}/></span>
              </div>
            </div>
          </Col>
          <Col xl="4" className='d-flex align-items-end'>
            <div><img height="100%" width="100%" src='https://pixydrops.com/taxiz-html/assets/images/testimonials/testimonials-happy-young-man-1-1.png' /></div>
          </Col>
        </Row>
    </div>
  );
};

export default Carousel;
