import { Row, Col } from 'react-bootstrap'
import React, { useState, useEffect } from 'react';
import { Menu, Phone } from 'lucide-react'
import Nav from './Nav';
import Aside from './Aside';
import i18n from '../lang/i18n'
import { useTranslation } from 'react-i18next';
import NavDropdown from 'react-bootstrap/NavDropdown';


function Header() {

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);



  const [scrolled, setScrolled] = useState(true);

  useEffect(() => {
    const handleScroll = () => {
      const isTop = window.scrollY < 10; // Vous pouvez modifier cette valeur selon votre besoin
      if (isTop !== scrolled) {
        setScrolled(isTop);
      }
    };

    document.addEventListener('scroll', handleScroll);
    return () => {
      document.removeEventListener('scroll', handleScroll);
    };
  });


  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  };

  const phoneNumber = '+33 758 065 128';

  return (
    <header className={scrolled ? 'w-100' : 'w-100 header-scrolled background-dark text-white'} style={{position:'fixed',top:"0",zIndex:"4"}}>
      <div className='container'>
        <Row className='justify-content-between align-items-center py-4 w-100'>
          <Col xs="10" sm="8" lg="2" className='d-flex align-items-center justify-content-center'>
            <img onClick={scrollToTop} width="100%" height="100%" src={scrolled ? 'https://taxitema.tokotema.com/wp-content/uploads/2023/12/Logo-TaxiTema-1-300x42.png' : 'https://taxitema.tokotema.com/wp-content/uploads/2023/12/Logo-TaxiTema-2-300x42.png'} alt='Logo de de societé de chauffeurs vtc à Paris' style={{ cursor: "pointer" }} title='Logo de de societé de chauffeurs vtc à Paris' loading='lazy' />
          </Col>
          <Col lg="6" className='p-0 d-none d-lg-block'>
            <Nav scrolled={scrolled} />
          </Col>
          <Col lg="2" className='d-flex align-items-center justify-content-center p-0 d-none d-lg-flex'>
            <div className='p-2 rounded-circle icon-bouncing background-main'>
              <Phone color='white' />
            </div>
            <div className='ms-3 d-flex flex-column'>
              <span className='main-text'>Appelez-nous</span>
              <a className='text-decoration-none text-white' href={`tel:${phoneNumber}`}>{phoneNumber}</a>
            </div>
          </Col>
          <Col lg="1" className="d-flex justify-content-center">
            <div onClick={handleShow} className={scrolled ? "text-black" : "text-main"} role='button'>
              <Menu />
            </div>
          </Col>
        </Row>
      </div>

      <Aside show={show} handleClose={handleClose}/>  
    </header>
   
  );
}

export default Header;
