import React from "react";

const Services = () => {
  const data = [
    {
      id: 1,
      img: "https://themeholy.com/wordpress/taxiar/wp-content/uploads/2023/09/service_3_1-4-1.jpg",
      width: "",
      height: "",
      content: "Les chauffeurs privés offrent un service de transport personnalisé où ils conduisent les clients d'un endroit à un autre en utilisant leur propre véhicule. Que ce soit pour des rendez-vous d'affaires, des soirées entre amis ou des déplacements quotidiens, les chauffeurs privés garantissent un trajet sûr et confortable.",
      title:"Tranferts aéroport"
    },
  ];
  return (
    <div className="container py-5">
      {data.map((item, index) => (
        <div className="position-relative" style={{ overflow: "hidden" }}>
          <div className="image-container">
            <img
              src={item.img}
              alt={item.content}
            />
            <div className="details-panel">
              <h2>{item.title}</h2>
              <p>{item.content}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Services;
