import React, { useState } from 'react';
import { useTransition, animated } from 'react-spring';

const data = [
  { title: "title 1", content: "..." },
  { title: "title 2", content: "..." },
  { title: "title 3", content: "..." }
];

const Carousel = () => {
  const [index, setIndex] = useState(0);

  console.log("Index:", index);

  const transitions = useTransition(data[index], item => item.title, {
    from: { opacity: 0, transform: 'translateX(-100%)' },
    enter: { opacity: 1, transform: 'translateX(0%)' },
    leave: { opacity: 0, transform: 'translateX(100%)' }
  });

  console.log("Transitions:", transitions);

  const next = () => {
    setIndex(prevIndex => (prevIndex + 1) % data.length);
  };

  const prev = () => {
    setIndex(prevIndex => (prevIndex === 0 ? data.length - 1 : prevIndex - 1));
  };

  console.log("Item:", transitions.map(({ item }) => item));

  return (
    <div className="carousel">
      {transitions.map(({ item, props, key }) => (
        item && (
          <animated.div key={key} style={props}>
            <div className="content">
              <h2>{item.title}</h2>
              <p>{item.content}</p>
            </div>
          </animated.div>
        )
      ))}
      <button onClick={prev}>Prev</button>
      <button onClick={next}>Next</button>
    </div>
  );
};

export default Carousel;
