import React, { useState } from 'react'

const Reviews = () => {

    const data = [
        {id:1,name:"John doe",content:"Whether You're Embarking On A Cross-Country Adventure Or Exploring A New Destination By Car, Finding The Right Car Rental Vehicle Is Essential For A Stress-Free Journey. Here Are Some Rental Car Tips To Help You Scors"},
        {id:2,name:"Jane smith",content:"Lorem ipsum dolor"},
        {id:3,name:"Ryan moore",content:"dring dring dring"},
      ]

      const [currentIndex, setCurrentIndex] = useState(0);

  const goToNextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === data.length - 1 ? 0 : prevIndex + 1
    );
  };

  const goToPrevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? data.length - 1 : prevIndex - 1
    );
  };


  return (
    <div className="slider">
    <button onClick={goToPrevSlide}>Précédent</button>
    <div className="slides-container">
      {data.map((item, index) => (
        <div
          key={index}
          className={
            index === currentIndex
              ? "slide active"
              : index === currentIndex - 1 || (currentIndex === 0 && index === data.length - 1)
              ? "slide prev"
              : "slide next"
          }
        >
          <p>{item}</p>
        </div>
      ))}
    </div>
    <button onClick={goToNextSlide}>Suivant</button>
  </div>
  )
}

export default Reviews
