import { useEffect, useRef, useState } from "react";
import { Row, Col } from "react-bootstrap";

function Background() {
  const data = [
    {
      step: "01",
      content: "Renseignez des informations correctes à popos de votre trajet",
    },
    {
      step: "02",
      content: "Attendez notre appel pour confimer votre réservation",
    },
    { step: "03", content: "Profitez de votre voyage" },
  ];

  const [index, setIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % data.length);
    }, 1500);

    return () => clearInterval(intervalId);
  }, [index, data.length]);

  const ajouterClasse = (i) => {
    return i === index ? "step" : "";
  };

  const handleScroll = (id) => {
    const element = document.getElementById(id);
    element.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <main className="background container">
      <Row>
        <Col
          lg="6"
          className="p-5 d-flex flex-column justify-content-center align-items-start"
        >

            <div className="fw-bold text-black" style={{fontSize:"600%"}}>
              Besoin de vous déplacer à Paris ?
            </div>
            <p className="fs-4 fw-medium my-3">
              Découvrez l'exquise qualité de notre service de transport où notre
              engagement garantit que votre trajet soit bien plus qu'une simple
              course grâce à nos chauffeurs privés sont disponibles pour vous
              24h/7j.
            </p>
            <div className="fs-5">
              {data.map((item, i) => (
                <div key={i} className="d-flex my-3">
                  <span className={`fs-3 fw-bold ${ajouterClasse(i)}`}>
                    {item.step}.
                  </span>
                  <span className="mx-3 fw-light">{item.content}</span>
                </div>
              ))}
            </div>
            <div
              role="button"
              className="main-btn-outline p-3 px-5 mt-3 fs-6 fw-light"
              onClick={() => handleScroll("contact")}
            >
              Réserver dés maintenant
            </div>
        </Col>
        <Col lg="6">
          <div>
            <img
              src="https://drively.codelayers.net/images/welcome_image.jpg"
              alt="chauffeur privé paris"
              style={{ objectFit: "cover" }}
              width="100%"
              height="100%"
              title="chauffeur privé paris"
            />
          </div>
        </Col>
      </Row>
    </main>
  );
}

export default Background;
