const Nav = () => {
  const data = [
    { title: "À propos", value: "about" },
    { title: "Tarifs", value: "tarifs" },
    { title: "Contact", value: "contact" },
  ];

  const handleScroll = (id) => {
    const element = document.getElementById(id);
    element.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <nav className="w-100 d-flex justify-content-around nav ">
      <ul>
      {data.map((item, index) => (
        <div key={index} onClick={() => handleScroll(item.value)}>{item.title}</div>
      ))}
      </ul>
    </nav>
  );
};

export default Nav;
