import React, { useState } from 'react';

// Composant pour afficher les informations de base dans une liste
const ListItem = ({ item, onItemClick }) => {
  return (
    <div onClick={() => onItemClick(item)}>
      {item.name} {/* Supposons que chaque objet a un attribut "name" */}
    </div>
  );
};

// Composant pour afficher les détails dans une modale
const Modal = ({ item, onClose }) => {
  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h2>{item.name}</h2>
        <p>Autres détails : {item.otherDetails}</p> {/* Ajoutez les détails supplémentaires ici */}
      </div>
    </div>
  );
};

// Composant principal
const App = () => {
  const [selectedItem, setSelectedItem] = useState(null);

  // Fonction pour ouvrir la modale avec les détails de l'élément sélectionné
  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  // Fonction pour fermer la modale
  const closeModal = () => {
    setSelectedItem(null);
  };

  // Supposons que vous avez un tableau d'objets appelé "data"
  const data = [
    { id: 1, name: 'Objet 1', otherDetails: 'Détails de l\'objet 1' },
    { id: 2, name: 'Objet 2', otherDetails: 'Détails de l\'objet 2' },
    // Ajoutez d'autres objets au besoin
  ];

  return (
    <div>
      <h1>Liste des objets :</h1>
      {data.map((item) => (
        <ListItem key={item.id} item={item} onItemClick={handleItemClick} />
      ))}
      {selectedItem && <Modal item={selectedItem} onClose={closeModal} />}
    </div>
  );
};

export default App;
