import { Quote } from 'lucide-react';
import React, { useState } from 'react';

const data = [
  { name: "title 1", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum." },
  { name: "title 2", content: "..." },
  { name: "title 3", content: "..." }
];

const Carousel = () => {
  const [index, setIndex] = useState(0);

  const next = () => {
    setIndex(prevIndex => (prevIndex + 1) % data.length);
  };

  const prev = () => {
    setIndex(prevIndex => (prevIndex === 0 ? data.length - 1 : prevIndex - 1));
  };

  return (
    <div className='container border p-5'>
        <div className="position-relative  py-5" style={{overflow:"hidden"}}>
            <div className="slider" style={{ transform: `translateX(-${index * 100}%)` }}>
                {data.map((item, idx) => (
                    <div key={idx} className="slide">
                        <div className='main-text'><Quote size={100} strokeWidth={1}/></div>
                        <p>{item.content}</p>
                        <h2>{item.name}</h2>
                        <p>{item.content}</p>
                    </div>
                ))}
            </div>
            <button onClick={prev}>Prev</button>
            <button onClick={next}>Next</button>
        </div>
    </div>
  );
};

export default Carousel;
