import { useEffect, useRef, useState } from "react";
import { Row, Col } from "react-bootstrap";
import Skills from "../components/Skills";
import Clients from "../components/Clients";

function About() {
  // const data = [
  //     {icon:<User size={40} strokeWidth={1.5}/>,title:"Gérant", content:"Zeraoui abdeslam"},
  //     {icon:<Mail size={40} strokeWidth={1.5}/>,title:"Courrier", content:"chauffeursvtc.paris@gmail.com"},
  //     {icon:<Smartphone size={40} strokeWidth={1.5}/>,title:"Téléphone", content:"+33 758 065 128"},
  //     {icon:<MapPin size={40} strokeWidth={1.5}/>,title:"Adresse", content:"Paris, france"},
  // ]



  const services = [
    {
      title: "Tarifs",
      content:
        "Profitez des <strong>tarifs vtc pas cher</strong>, compétitifs qui peuvent étre ajusté en fonction du trafic et de la durée, vous assurant la meilleure offre quelles que soient les conditions.",
    },
    {
      title: "Attente",
      content:
        "Pour vous permettre de <strong>voyager</strong> sans stress ni précipitation, Une prise en charge à l’aéroport ou ailleurs comprend un temps d’attente gratuit.",
    },
    {
      title: "Flexibilité",
      content:
        "Que ce soit des <strong>transferts à l'aéroport, des déplacements professionnels, des visites, urgence, ou une livraison express</strong> profitez de notre gamme complète.",
    },
  ];

  

  

  return (
    <section className="background-dark" id="about">
      <div className="container text-white py-5">
        <Skills/>
        <Clients/>  

        <Row>
            {services.map((item,index)=>(
                <Col sm="4" className="my-3">
                    <div className="d-flex flex-column">
                        <div className="h3 mt-4">{item.title}</div>
                        <p dangerouslySetInnerHTML={{__html: item.content}}>
                            
                        </p>
                    </div>
                </Col>
            ))}
                       
        </Row>
      </div>
    </section>
  );
}

export default About;
