import React, { useState, useEffect } from 'react';

const Nav = () => {

    const [scrolled, setScrolled] = useState(true);

  useEffect(() => {
    const handleScroll = () => {
      const isTop = window.scrollY < 10; // Vous pouvez modifier cette valeur selon votre besoin
      if (isTop !== scrolled) {
        setScrolled(isTop);
      }
    };

    document.addEventListener('scroll', handleScroll);
    return () => {
      document.removeEventListener('scroll', handleScroll);
    };
  });

  const handleScroll = (id) => {
    const element = document.getElementById(id);
    element.scrollIntoView({ behavior: "smooth" });
  }


  return (
    <nav className="w-100 d-flex justify-content-around nav ">
      <div onClick={() => handleScroll("about")}>A propos</div>
      <div onClick={() => handleScroll("tarifs")}>Tarifs</div>
      <div onClick={() => handleScroll("contact")}>Contact</div>
    </nav>
  );
};

export default Nav;
