import React from 'react'

const Reviews = () => {
    const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === texts.length - 1 ? 0 : prevIndex + 1));
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? texts.length - 1 : prevIndex - 1));
  };

  const texts = ['hello world', 'where is brayan','lorem ipsum dolor']

  return (
     <div className="text-scroller-container">
      <div className="text-scroller-item">
        {texts.map((text, index) => (
          <div
            key={index}
            className={`text-item ${index === currentIndex ? 'active' : ''}`}
          >
            {text}
          </div>
        ))}
      </div>
      <div className="button-container">
        <button onClick={handlePrev}>Précédent</button>
        <button onClick={handleNext}>Suivant</button>
      </div>
    </div>
  )
}

export default Reviews
