import React from 'react'

const Services = () => {

  const [selectedItem, setSelectedItem] = useState(null);

  // Fonction pour ouvrir la modale avec les détails de l'élément sélectionné
  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  // Fonction pour fermer la modale
  const closeModal = () => {
    setSelectedItem(null);
  };


  const data = [
    { id: 1, name: 'Objet 1', otherDetails: 'Détails de l\'objet 1' },
    { id: 2, name: 'Objet 2', otherDetails: 'Détails de l\'objet 2' },
    // Ajoutez d'autres objets au besoin
  ];


  return (
    <div id='services' className='container py-5'>
      Services

      <div>
      <h1>Liste des objets :</h1>
      {data.map((item) => (
        <ListItem key={item.id} item={item} onItemClick={handleItemClick} />
      ))}
      {selectedItem && <Modal item={selectedItem} onClose={closeModal} />}
    </div>
    </div>
  )
}

export default Services
