import { ArrowRight } from 'lucide-react';
import React from 'react'



const Links = () => {
  const data = [
    { title: "Accueil", value: "home" },
    { title: "À propos", value: "about" },
    { title: "Services", value: "booking" },
    { title: "Tarifs", value: "tarifs" },
    { title: "Réservation", value: "booking" },
  ];

  const handleScroll = (id) => {
    const element = document.getElementById(id);
    element.scrollIntoView({ behavior: "smooth" });
  };


  return (
    <nav className="w-100">
        <div className='fs-6 fw-bold'>Liens utiles</div>
        <ul className="d-flex flex-column justify-content-around nav list-unstyled my-3">
            {data.map((item, index) => (
                <li className="text-white my-2" role="button" key={index} onClick={() => handleScroll(item.value)}><ArrowRight size={20} strokeWidth={1.5}/> {item.title}</li>
            ))}
        </ul>
    </nav>
  )
}

export default Links
