import React, { useState } from 'react'

const Reviews = () => {
    const [currentIndex, setCurrentIndex] = useState(0);

    const data = [
        {id:1,name:"John doe",content:"Whether You're Embarking On A Cross-Country Adventure Or Exploring A New Destination By Car, Finding The Right Car Rental Vehicle Is Essential For A Stress-Free Journey. Here Are Some Rental Car Tips To Help You Scors"},
        {id:2,name:"Jane smith",content:"Lorem ipsum dolor"},
        {id:3,name:"Ryan moore",content:"dring dring dring"},
      ]

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1));
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? data.length - 1 : prevIndex - 1));
  };



  return (
    <div className="container">
        <div className="bg-danger">
        <div className='bg-dark d-flex justify-content-center'>
            {data.map((item, index) => (
                <div
                key={index}
                className={`w-100 bg-success text-item ${index === currentIndex ? 'active' : ''}`}
                >
                {item.content}
                </div>
            ))}
            </div>
        </div>
        <div className="button-container">
        <button onClick={handlePrev}>Précédent</button>
        <button onClick={handleNext}>Suivant</button>
        </div>
  </div>
  )
}

export default Reviews
