import React, { useState } from 'react'

const Reviews = () => {

    const data = [
        {id:1,name:"John doe",content:"Whether You're Embarking On A Cross-Country Adventure Or Exploring A New Destination By Car, Finding The Right Car Rental Vehicle Is Essential For A Stress-Free Journey. Here Are Some Rental Car Tips To Help You Scors"},
        {id:2,name:"Jane smith",content:"Lorem ipsum dolor"},
        {id:3,name:"Ryan moore",content:"dring dring dring"},
      ]

      const [currentIndex, setCurrentIndex] = useState(0);

      const goToNextSlide = () => {
        setCurrentIndex((prevIndex) =>
          prevIndex === images.length - 1 ? 0 : prevIndex + 1
        );
      };

      const goToPrevSlide = () => {
        setCurrentIndex((prevIndex) =>
          prevIndex === 0 ? images.length - 1 : prevIndex - 1
        );
      };

      const images = ['https://pixydrops.com/taxiz-html/assets/images/blog/blog-1-3.jpg',"https://pixydrops.com/taxiz-html/assets/images/blog/blog-1-1.jpg"]


  return (
    <div className="slider">
      <button onClick={goToPrevSlide}>Précédent</button>
      <div className="slides-container">
        {images.map((image, index) => (
          <div
            key={index}
            className={
              index === currentIndex
                ? "slide active"
                : index === currentIndex - 1 || (currentIndex === 0 && index === images.length - 1)
                ? "slide prev"
                : "slide next"
            }
          >
            <img src={image} alt={`Slide ${index}`} />
          </div>
        ))}
      </div>
      <button onClick={goToNextSlide}>Suivant</button>
    </div>
  )
}

export default Reviews
