import { useEffect, useRef, useState } from "react";
import { Row, Col } from "react-bootstrap";

function About() {
  // const data = [
  //     {icon:<User size={40} strokeWidth={1.5}/>,title:"Gérant", content:"Zeraoui abdeslam"},
  //     {icon:<Mail size={40} strokeWidth={1.5}/>,title:"Courrier", content:"chauffeursvtc.paris@gmail.com"},
  //     {icon:<Smartphone size={40} strokeWidth={1.5}/>,title:"Téléphone", content:"+33 758 065 128"},
  //     {icon:<MapPin size={40} strokeWidth={1.5}/>,title:"Adresse", content:"Paris, france"},
  // ]

  const data = [
    { title: "Expérimentés", value: 90 },
    { title: "Fiables", value: 100 },
    { title: "Qualifiés", value: 95 },
    { title: "Flexibles", value: 90 },
  ];

  const services = [
    {
      title: "Tarifs",
      content:
        "Profitez des <strong>tarifs vtc pas cher</strong>, compétitifs qui peuvent étre ajusté en fonction du trafic et de la durée, vous assurant la meilleure offre quelles que soient les conditions.",
    },
    {
      title: "Attente",
      content:
        "Pour vous permettre de <strong>voyager</strong> sans stress ni précipitation, Une prise en charge à l’aéroport comprend un temps d’attente gratuit.",
    },
    {
      title: "Flexibilité",
      content:
        "Que ce soit des <strong>transferts à l'aéroport, des déplacements professionnels, des visites, urgence, ou une livraison express</strong> profitez de notre gamme complète.",
    },
  ];

  const finalCount = 90;

  const [count, setCount] = useState(0);
  const counterRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (counterRef.current) {
        const top = counterRef.current.getBoundingClientRect().top;
        const isVisible = top < window.innerHeight;
        setIsVisible(isVisible);
      }
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  useEffect(() => {
    if (isVisible) {
      const increment = () => {
        if (count < finalCount) {
          setCount((prevCount) => prevCount + 1);
        }
      };

      const interval = setInterval(increment, 20); // Vitesse de comptage, en millisecondes

      return () => clearInterval(interval);
    }
  }, [count, finalCount, isVisible]);

  return (
    <section className="background-dark" id="about">
      <div className="container text-white py-5">
        <Row className="justify-content-between align-items-center">
          <Col lg="6" className="my-3">
            <div className="px-3">
              <div className="fs-5 main-text">À propos de nous</div>
              <div className="fs-1 fw-bold">
                Renforcez votre expérience de voyage avec TaxiTema
              </div>
              <p className="fw-light fs-5 my-3">
                Nous sommes une équipe de chauffeurs vtc dévouées et déterminés
                à garantir un <strong>trajet à paris</strong> sur et
                confortable. Faites-nous confiance pour un service de{" "}
                <strong>transport</strong> haut de gamme et une expérience
                client exceptionnelle.
              </p>
            </div>
          </Col>
          <Col lg="5" className="my-3">
            {data.map((item, index) => (
              <div className="my-4">
                <div className="d-flex justify-content-between fs-5">
                  <span>{item.title}</span>
                  <span>{item.value}%</span>
                </div>
                <div class="progress-item mt-2">
                  <div
                    style={{ width: `${item.value}%` }}
                    class="progress-content"
                    id="myProgressBar"
                  ></div>
                </div>
              </div>
            ))}
          </Col>
        </Row>
        <Row className="my-5 align-items-center">
          <Col lg="4" className="my-3">
            <div
              style={{ height: "350px" }}
              className="d-flex flex-column justify-content-center align-items-center border p-5"
            >
              <div
                ref={counterRef}
                className={`fw-bold main-text counter ${
                  isVisible ? "visible" : ""
                }`}
                style={{ fontSize: "600%" }}
              >
                {count} %
              </div>
              <div className="fs-2">Client satisfaits</div>
            </div>
          </Col>
          <Col lg="8" className="my-3">
            <img
            alt="Illustration d'un transfert"
              src="https://taxitema.tokotema.com/wp-content/uploads/2023/12/selective-focus-of-smiling-businessman-and-taxi-dr-2023-11-27-05-13-01-utc-1.jpg"
              width="100%"
              height="100%"
              style={{ maxHeight: "350px" }}
            />
          </Col>
        </Row>

        <Row>
            {services.map((item,index)=>(
                <Col sm="4">
                    <div className="d-flex flex-column">
                        <div className="h3 mt-4">Tarifs</div>
                        <p>
                            Profitez des <strong>tarifs vtc pas cher</strong>, compétitifs
                            qui peuvent étre ajusté en fonction du trafic et de la durée,
                            vous assurant la meilleure offre quelles que soient les
                            conditions.
                        </p>
                    </div>
                </Col>
            ))}
                       
          <Col sm="4" lg="4">
            <div className="d-flex flex-column">
              <div className="h3 mt-4">Tarifs</div>
              <p>
                Profitez des <strong>tarifs vtc pas cher</strong>, compétitifs
                qui peuvent étre ajusté en fonction du trafic et de la durée,
                vous assurant la meilleure offre quelles que soient les
                conditions.
              </p>
            </div>
          </Col>
          <Col sm="4" lg="4">
            <div className="d-flex flex-column">
              <div className="h3 mt-4">Attente</div>
              <p>
                pour vous permettre de <strong>voyager</strong> sans stress ni
                précipitation, Une prise en charge à l’aéroport comprend un
                temps d’attente gratuit.
              </p>
            </div>
          </Col>
          <Col sm="4" lg="4">
            <div className="d-flex flex-column">
              <div className="h3 mt-4">Flexibilité</div>
              <p>
                Que ce soit des{" "}
                <strong>
                  transferts à l'aéroport, des déplacements professionnels, des
                  visites, urgence, ou une livraison express
                </strong>{" "}
                profitez de notre gamme complète.
              </p>
            </div>
          </Col>
        </Row>
      </div>
    </section>
  );
}

export default About;
